import java.util.ArrayList;
import java.util.Arrays;
import java.util.List;

public class Main {
    public static void main(String[] args) {

        String INPUT = "\n" +
                "\n" +
                "\n" +
                "\n" +
                "";

        List<String> inputList = new ArrayList<>(Arrays.asList(INPUT.split("\n")));

        System.out.println(partOne(inputList));
        System.out.println(partTwo(inputList));
    }

    public static String partOne(List<String> input){

        String code = "";

        char[][] keypad = {
                new char[] {'1','2','3'},
                new char[] {'4','5','6'},
                new char[] {'7','8','9'}
        };

        int x = 1;
        int y = 1;

        for (String line : input) {
            for (int i = 0; i < line.length(); i++) {
                switch (line.charAt(i)) {
                    case 'U':
                        if (y > 0) y--;
                        break;
                    case 'L':
                        if (x > 0) x--;
                        break;
                    case 'D':
                        if (y < 2) y++;
                        break;
                    case 'R':
                        if (x < 2) x++;
                        break;
                    default:
                        break;
                }
            }
            code += keypad[y][x];
        }
        return code;
    }

    public static String partTwo(List<String> input){

        String code = "";

        char[][] keypad = {
                new char[] {'X','X','1','X','X'},
                new char[] {'X','2','3','4','X'},
                new char[] {'5','6','7','8','9'},
                new char[] {'X','A','B','C','X'},
                new char[] {'X','X','D','X','X'}
        };

        int x = 0;
        int y = 2;

        for (String line : input) {
            for (int i = 0; i < line.length(); i++) {
                switch (line.charAt(i)) {
                    case 'U':
                        if (y > 0 && keypad[y-1][x] != 'X') y--;
                        break;
                    case 'L':
                        if (x > 0 && keypad[y][x-1] != 'X') x--;
                        break;
                    case 'D':
                        if (y < 4 && keypad[y+1][x] != 'X') y++;
                        break;
                    case 'R':
                        if (x < 4 && keypad[y][x+1] != 'X') x++;
                        break;
                    default:
                        break;
                }
            }
            code += keypad[y][x];
        }

        return code;
    }
}
